import React, {Component} from 'react';
import {CardList} from './components/card-list/card-list.component';
import {SearchBox} from './components/search-box/search-box.component';
import './App.css';





class App extends Component {



  constructor(){
    super();

    this.state= {
      pokemonBalls:[],//array to hold the pokemonBalls,
      pokedexEntry: ''
    };

  }
  componentDidMount(){
    fetch('https://pokeapi.co/api/v2/pokemon?limit=1000')//gets the first 1000 pokemon
     .then(response=>response.json()) 
     .then(name => this.setState({pokemonBalls:name.results}))
  }

  render(){
    const { pokemonBalls, pokedexEntry} = this.state;
    const fileteredPokemons  = pokemonBalls.filter(pokemon  => pokemon.name.toLowerCase().includes(pokedexEntry.toLowerCase()))
    return(
      <div className="App">
        <SearchBox
            placeholder='Enter a pokemon name' 
            handleChange={e => this.setState({ pokedexEntry: e.target.value })}
            />
        <CardList pokemonBalls={fileteredPokemons}/>
    </div>

    );
  }
}

export default App;
